        this.state = {
            data: props.datalist
        }
    }

    componentDidMount() {
        this.drawChart();
    }

    drawChart() {
        const data = [12, 5, 6, 6, 9, 10];
        const svg = d3.select(this.refs.datas).append("svg").attr("width", 700).attr("height", 300);
        svg.selectAll("rect")
  .data(data)
