import React, { Component } from 'react';
import axios from 'axios';

export default class Record extends Component {
    state = {
        recordsList: []
    }

    componentDidMount() {
        axios.get("http://localhost:3003/record")
            .then(res => {
                const recordsData = res.data;
                console.log(recordsData);
                this.setState({
                    recordsList : recordsData
                });
            })
    }

    deleteRecord(e, record) {
        e.preventDefault();

        axios.get('http://localhost:3003/record/delete/' + record._id)
            .then(res => {
                console.log(res);
                console.log(res.data);
            })
        window.location = '/';
    }

    render() {
        return(
        <div className="record">
            <h1>My playlist</h1>
            <table>
                <thead>
                    <tr><th></th><th className='desc-h-col'>DATE</th><th className='desc-h-col'>AMOUNT</th><th className='desc-h-col'>CATEGORY</th><th className='button-col'></th></tr>
                </thead>
                <tbody>
                    {this.state.recordsList.map(record =>
                        <tr><td className='counterCell'></td><td className='desc-col'>{record.date}</td><td className='desc-col'>{record.amount}</td><td className='desc-col'>{record.category}</td><td className='button-col'><button onClick = {(e) => {this.deleteRecord(e, record)}}>delete</button></td></tr>
                    )}
                </tbody>
            </table>
        </div>
    )
    }
}
