import React, { Component } from 'react';
import * as d3 from 'd3'

class Charts extends Component {
    constructor(props) {
        super(props);

        // this.state = {
        //     data: props.datalist
        // }

        this.data = props.datalist;
        this.month = props.month;
        this.year = props.year;
        this.total = 0;
    }

    componentDidMount() {
        this.drawChart();
    }

    getTotalNumber() {


    }

    getClassifiedData() {
        var countMap = {};
        var retList = [];
        var total = 0;
        this.data.forEach(item => {
            countMap[item.category] ? 
                (countMap[item.category] = countMap[item.category] + item.amount) : 
                (countMap[item.category] = item.amount);
            total = total + item.amount;
        });
        this.total = total;
        for (var category in countMap) {
            var count = countMap[category];
            var percentage = (count / total) * 100;
            retList.push({"name": category, "count": count, "percentage" : percentage.toFixed(2)})
        }
        console.log(countMap);
        console.log(retList);
        console.log(retList.map(d => d.name));
        return retList;
        //return d3.interpolat
    }

    drawChart() {
        const classifiedData = this.getClassifiedData();
        console.log(this.data);
        console.log(classifiedData);
        const svgWidth = 1200;
        const svgHeight = 600;
        const margin = 50;

        var pie = d3.pie().sort(null).value(d => d.value);

        var radius = Math.min(svgWidth, svgHeight) / 2 - margin;

        const pieSvg = d3.select(this.refs.pie)
            .append("svg").attr("width", svgWidth).attr("height", svgHeight)
            .append("g").attr("transform", "translate(" + svgWidth / 2 + "," + svgHeight / 2 + ")");

        // set the color scale
        //console.log(d3.quantize(t => d3.interpolateSpectral(t * 0.8 + 0.1), classifiedData.length).reverse());
        var color = d3.scaleOrdinal()
            .domain(classifiedData.map(d => d.name))
            .range(d3.quantize(t => d3.interpolateSpectral(t * 0.8 + 0.1), classifiedData.length).reverse());

var data_ready = pie(classifiedData)

// Build the pie chart: Basically, each part of the pie is a path that we build using the arc function.
pieSvg
.selectAll('whatever')
.data(data_ready)
.enter()
.append('path')
.attr('d', d3.arc()
  .innerRadius(100)         // This is the size of the donut hole
  .outerRadius(radius)
)
.attr('fill', 'red')  //function(d){ return(color(d.data.name)) }
.attr("stroke", "black")
.style("stroke-width", "2px")
.style("opacity", 0.7)

pieSvg.append("text")
.attr("text-anchor", "middle")
.text("$" + this.total.toFixed(2));

    }

    render() {
        return(
        <div className="pie_chart">
            <h1>Expenses Summary</h1>
            <div className="show_year_month">
                <span>YEAR: {this.year == '0' ? 'All' : this.year}</span>
                <span>MONTH: {this.month == '0' ? 'All' : this.month}</span>
            </div>
            <div ref="pie"></div>
        </div>
        )
    }
}
export default Charts;
