import React, { Component } from 'react';
import * as d3 from 'd3'

class Charts extends Component {
    constructor(props) {
        super(props);

        // this.state = {
        //     data: props.datalist
        // }

        this.data = props.datalist;
        this.month = props.month;
        this.year = props.year;
        this.total = 0;
    }

    componentDidMount() {
        this.drawChart();
    }

