import React, { Component } from 'react';
import axios from 'axios';

class UpdateRecords extends Component {
    constructor(props) {
        super(props);

        this.onChangeDate = this.onChangeDate.bind(this);
        this.onChangeAmount = this.onChangeAmount.bind(this);
        this.onChangeCategory = this.onChangeCategory.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            id: props.location.updateProps._id,
            date: '',
            amount: '',
            category: '',
            originDate: new Date(props.location.updateProps.date),
            originAmount: props.location.updateProps.amount,
            originCategory: props.location.updateProps.category
        }
    }

    componentDidMount() {
        this.setState({
            date: this.state.originDate.getFullYear() + '-' + this.state.originDate.getMonth() + 1 + '-' + this.state.originDate.getDate(),
            amount: this.state.originAmount,
            // category: this.state.originCategory
            category: this.state.originDate.getFullYear() + '-' + this.state.originDate.getMonth() + 1 + '-' + this.state.originDate.getDate()
        });
    }

    onChangeDate(e) {
        this.setState({
            date: e.target.value
        });
    }

    onChangeAmount(e) {
        this.setState({
            amount: e.target.value
        });
    }

    onChangeCategory(e) {
        this.setState({
            category: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const record = {
            date: this.state.date,
            amount: this.state.amount,
            category: this.state.category
        }
        axios.post('http://localhost:3003/update/' + this.state.id, record)
            .then(res => {
                console.log(res);
                console.log(res.data);
            })

        window.location = '/';
    }

    render() {
        return(
            <div className="record">
            <h2>Update Expense</h2>
            <form onSubmit={this.onSubmit}>
                <div className="form-field">
                    <label>Date </label>
                    <input type="date" name="date" placeholder="" value={this.state.date} onChange={this.onChangeDate} required />
                </div>
                <div className="form-field">
                    <label>Amount </label>
                    <input type="text" name="amount" placeholder="" value={this.state.amount} onChange={this.onChangeAmount} required/>
                </div>
                <div className="form-field">
                    <label>Category </label>
                    <input type="text" name="category" placeholder="" value={this.state.category} onChange={this.onChangeCategory} required />
                </div>
                <input className="submit" type="submit" value="Submit"></input>
            </form>
            </div>
        )
    }
}

export default UpdateRecords;
