import React, { Component } from 'react';
import axios from 'axios';
import dateFormat from 'dateformat';
import { Link } from 'react-router-dom';

export default class Record extends Component {
    state = {
        recordsList: [],
        sum: 0,
        selectedYear: '',
        selectedMonth: ''
    }

    componentDidMount() {
        if (this.state.selectedYear == '' && this.state.selectedMonth == '') {
            axios.get("http://localhost:3003/record")
            .then(res => {
                const recordsData = res.data;
                console.log(recordsData);
                this.setState({
                    recordsList : recordsData,
                    sum: this.calculateSum(recordsData)
                });
            })
        }
        // axios.get("http://localhost:3003/record")
        //     .then(res => {
        //         const recordsData = res.data;
        //         console.log(recordsData);
        //         this.setState({
        //             recordsList : recordsData
        //         });
        //     })
    }

    deleteRecord(e, record) {
        e.preventDefault();

        axios.get('http://localhost:3003/record/delete/' + record._id)
            .then(res => {
                console.log(res);
                console.log(res.data);
            })
        window.location = '/';
    }

    getLocalFormatDate(d) {
        const utcDate = new Date(d);
        const localDate = new Date(utcDate.getTime() + utcDate.getTimezoneOffset() * 60000);
        return dateFormat(localDate, "ddd mmm dd, yyyy");
    }

    calculateSum(dataList) {
        //return dataList.reduce((a, b) => parseInt(a.amount) + parseInt(b.amount), 0)
        var sum = 0;
        dataList.forEach(item => {
            sum = sum + Number(item.amount);
        });
        return sum;
    }

    render() {
        return(
        <div className="record">
            <h1>My playlist</h1>
            <div className="expense_sum">
                <label>total expense: </label>
                <span>{this.state.sum.toFixed(2)}</span>
            </div>
            <table>
                <thead>
                    <tr>
                        <th></th>
                        <th className='desc-h-col'>DATE</th>
                        <th className='desc-h-col'>AMOUNT</th>
                        <th className='desc-h-col'>CATEGORY</th>
                        <th className='button-col'></th>
                    </tr>
                </thead>
                <tbody>
                    {this.state.recordsList.map(record =>
                        <tr>
                            <td className='counterCell'></td>
                            <td className='desc-col'>{this.getLocalFormatDate(record.date)}</td>
                            <td className='desc-col'>{record.amount}</td>
                            <td className='desc-col'>{record.category}</td>
                            <td className='button-col'>
                                <Link to={
                                    {
                                        pathname: '/update',
                                        updateProps: record
                                    }
                                }><button>update</button></Link>
                            </td>
                            <td className='button-col'><button onClick = {(e) => {this.deleteRecord(e, record)}}>delete</button></td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    )
    }
}
